INSERT INTO news_articles (title, slug, excerpt, content, category, source_name, source_url, is_published, is_featured, published_at) VALUES 
('Portobello vende su participación en Telepizza a un fondo canadiense por 120M€', 'portobello-telepizza-fondo-canadiense-120m', 'El fondo de capital riesgo español cierra la desinversión de su participación en la cadena de pizzerías tras cuatro años de inversión estratégica.', 'El fondo de capital riesgo español Portobello Capital ha completado la venta de su participación en Telepizza a un fondo de inversión canadiense por un importe cercano a los 120 millones de euros. La operación supone una de las desinversiones más relevantes del año en el sector de alimentación.', 'Private Equity', 'Expansión', 'https://expansion.com/empresas/2025/01/telepizza-portobello', true, false, NOW()),
('Cinven completa la adquisición de Hotelbeds valorada en 4.500M€', 'cinven-hotelbeds-adquisicion-4500m', 'El gigante de distribución hotelera pasa a manos del fondo británico en una de las mayores operaciones del sector turístico español.', 'Cinven ha cerrado la adquisición de Hotelbeds, la mayor plataforma B2B de distribución hotelera del mundo con sede en Palma de Mallorca. La operación está valorada en 4.500 millones de euros y representa una de las mayores transacciones del sector turístico en España.', 'M&A', 'Capital & Corporate', 'https://capitalandcorporate.com/cinven-hotelbeds', true, true, NOW() - interval '1 day'),
('ProA Capital invierte 30M€ en Grupo Hospitales Parque', 'proa-capital-hospitales-parque-30m', 'La firma de private equity española apuesta por el sector sanitario con esta nueva inversión estratégica en centros hospitalarios.', 'ProA Capital ha anunciado una inversión de 30 millones de euros en Grupo Hospitales Parque, compañía especializada en la gestión de centros hospitalarios. Esta operación refuerza la estrategia del fondo de apostar por el sector sanitario español.', 'Private Equity', 'El Economista', 'https://eleconomista.es/proa-hospitales', true, false, NOW() - interval '2 days'),
('Corpfin Capital adquiere el 60% de Roams, comparador de servicios financieros', 'corpfin-roams-comparador-fintech', 'La operación valora la fintech española en más de 50 millones de euros, consolidando el interés del PE en el sector.', 'Corpfin Capital ha adquirido una participación mayoritaria del 60% en Roams, el comparador español de servicios financieros y telecomunicaciones. La operación valora la compañía en más de 50 millones de euros y consolida el interés del private equity en el sector fintech.', 'Venture Capital', 'Expansión', 'https://expansion.com/startups/roams-corpfin', true, false, NOW() - interval '3 days');